import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Products from './components/Products/Products';
import RegisterModal from './components/Login/RegisterModal';
import FaqSection from './components/FAQ/FAQSection';
import BottomNavbar from './components/BNB/bottomNavBar';
import LoginModal from './components/Login/LoginModal';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import { FaShoppingCart, FaExchangeAlt, FaDonate, FaSearch, FaShareAlt } from 'react-icons/fa';

// üîπ COMPONENTE: Podr√°s
const Podras = () => (
  <div className="aliados-container-custom rounded-3xl p-8 bg-[#1A1230] shadow-[0_0_20px_2px_rgba(176,70,255,0.6)]">
    <h2 className="podras-title">PODR√ÅS...</h2>
    <div className="podras-options">
      <div className="podras-option"><FaShoppingCart size={40} /><p>Comprar</p></div>
      <div className="podras-option"><FaExchangeAlt size={40} /><p>Vender</p></div>
      <div className="podras-option"><FaDonate size={40} /><p>Donar</p></div>
      <div className="podras-option"><FaSearch size={40} /><p>Explorar</p></div>
      <div className="podras-option"><FaShareAlt size={40} /><p>Compartir ideas</p></div>
    </div>
  </div>
);

// üîπ COMPONENTE: Rese√±as
const Rese√±as = () => (
  <div className="reviews-section">
    <div className="aliados-container-custom rounded-3xl p-8 bg-[#1A1230] shadow-[0_0_20px_2px_rgba(176,70,255,0.6)]">
      <h2 className="reviews-title">RESE√ëAS</h2>
      <Swiper
        spaceBetween={20}
        slidesPerView={1}
        breakpoints={{
          768: { slidesPerView: 2 },
          1024: { slidesPerView: 3 }
        }}
      >
        {['‚òÖ‚òÖ‚òÖ‚òÜ‚òÜ', '‚òÖ‚òÖ‚òÖ‚òÜ‚òÜ', '‚òÖ‚òÖ‚òÖ‚òÖ‚òÜ', '‚òÖ‚òÖ‚òÜ‚òÜ‚òÜ', '‚òÖ‚òÖ‚òÖ‚òÖ‚òÜ', '‚òÖ‚òÖ‚òÖ‚òÖ‚òÖ'].map((stars, i) => (
          <SwiperSlide key={i}>
            <div className="review-card">
              <div className="review-stars">{stars}</div>
              <p className="review-text">Excelente plataforma, prob√© antes de suscribirme.</p>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  </div>
);

// üîπ COMPONENTE: Layout con Navbar y contenido seg√∫n ruta
const Layout = () => {
  const location = useLocation();
  const isProductsPage = location.pathname === "/productos";

  const [isLoginModalOpen, setIsLoginModalOpen] = useState(false);
  const [isRegisterModalOpen, setIsRegisterModalOpen] = useState(false);

  return (
    <div className="App">
      {/* Navbar din√°mica */}
      {!isProductsPage ? (
      // Navbar para todas las p√°ginas EXCEPTO /productos
      <nav className="navbar">
        <div className="navbar-brand">
          <span className="brand-R3">√önete a </span>
          <span className="brand-Unete">R3</span>
        </div>
        <div className="navbar-buttons">
          <button className="btn-login" onClick={() => setIsLoginModalOpen(true)}>Iniciar Sesi√≥n</button>
          <button className="btn-join" onClick={() => setIsRegisterModalOpen(true)}>Unirse</button>
        </div>
      </nav>
    ) : (
  // Navbar especial para /productos en la parte superior
  <nav className="navbar">
        <div className="navbar-brand">
          <span className="brand-R3">Bienvenido a </span>
          <span className="brand-Unete">R3</span>
    </div>
    <ul className="navbar-productos-links">
      <li><a href="/">Home</a></li>
